const inputControl=document.querySelectorAll(".input-control"),form=document.querySelector(".form");form.addEventListener("submit",e=>{e.preventDefault(),validateInputs()||form.submit()});const validateInputs=()=>{let e=!1;return inputControl.forEach(t=>{let r=t.querySelector("input"),o=t.querySelector(".error");validateInput(r,o)&&(e=!0)}),e},validateInput=(e,t)=>{let r=!1;if(""===e.value&&(setError(e,t,"Oops! This field cannot be empty"),r=!0),"email"===e.getAttribute("type")){let o=/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;""===e.value?(setError(e,t,"Oops! Please add your email"),r=!0):o.test(e.value)?removeError(e,t):(setError(e,t,"Oops! Please check your email"),r=!0)}return r},setError=(e,t,r)=>{let o=e.parentElement;o.classList.contains("input-control")?(o.classList.add("validation-error"),t.innerText=r):console.error("Parent element is not an input-control")},removeError=(e,t)=>{let r=e.parentElement;r.classList.contains("input-control")?(r.classList.remove("validation-error"),t.innerText=""):console.error("Parent element is not an input-control")};